// Parse Gemini response and convert to backend format
const result = items[0].json.result;

let parsed;
try {
  // Try to extract JSON from the response
  const jsonMatch = result.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found');
  }
} catch (error) {
  console.log('Parse error:', error);
  // Fallback if parsing fails
  parsed = {
    score: 5,
    cefr_level: 'B1',
    is_correct: false,
    feedback: 'Unable to analyze sentence properly.',
    corrected_sentence: items[0].json.body.sentence
  };
}

// Map fields to backend format
const output = {
  score: Number(parsed.score) || 5,
  cefr_level: String(parsed.cefr_level || 'B1').replace('/', '').split('/')[0], // Fix "A1/A2" to "A1"
  is_correct: Boolean(parsed.is_correct !== false && parsed.score >= 6),
  feedback: String(parsed.feedback || 'Good attempt!'),
  corrected_sentence: String(parsed.corrected_sentence || parsed.corrected_version || items[0].json.body.sentence),
  original_word: items[0].json.body.word,
  original_sentence: items[0].json.body.sentence
};

return { json: output };
