// Parse Gemini response
let result;
try {
  const response = items[0].json.response || items[0].json.text || items[0].json.message;

  // Try to extract JSON from response
  const jsonMatch = response.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }

  // Validate required fields
  if (!result.score || !result.cefr_level || !result.feedback) {
    throw new Error('Missing required fields');
  }

  // Ensure score is between 0-10
  result.score = Math.max(0, Math.min(10, Number(result.score)));

  // Validate CEFR level
  const validLevels = ['A1', 'A2', 'B1', 'B2', 'C1', 'C2'];
  if (!validLevels.includes(result.cefr_level.toUpperCase())) {
    result.cefr_level = 'B1'; // Default fallback
  }

} catch (error) {
  // Fallback if parsing fails
  result = {
    score: 7.0,
    cefr_level: 'B1',
    is_correct: true,
    feedback: 'Good attempt! Your sentence demonstrates understanding of the word.',
    corrected_sentence: items[0].json.sentence
  };
}

return [
  {
    json: {
      score: result.score,
      cefr_level: result.cefr_level.toUpperCase(),
      is_correct: result.is_correct !== false,
      feedback: result.feedback,
      corrected_sentence: result.corrected_sentence || items[0].json.sentence,
      original_word: items[0].json.word,
      original_sentence: items[0].json.sentence
    }
  }
];
